package TestaFarmacia;public class TestaFarmacia {    private String nome;    private int codigo;    private String tipo;    private float desconto;    private float preco;    public TestaFarmacia(String nome, int codigo, String tipo, float preco, float desconto) {        this.nome = nome;        this.codigo = codigo;        this.tipo = tipo;        this.desconto = desconto;        this.preco = preco;    }    public String getNome() {        return nome;    }    public TestaFarmacia setNome(String nome) {        this.nome = nome;        return this;    }    public int getCodigo() {        return codigo;    }    public TestaFarmacia setCodigo(int codigo) {        this.codigo = codigo;        return this;    }    public String getTipo() {        return tipo;    }    public TestaFarmacia setTipo(String tipo) {        this.tipo = tipo;        return this;    }    public float getDesconto() {        return desconto;    }    public TestaFarmacia setDesconto(float desconto) {        this.desconto = desconto;        return this;    }    public float getPreco() {        return preco;    }    public TestaFarmacia setPreco(float preco) {        this.preco = preco;        return this;    }    public void imprimir() {        System.out.println("\n***************************************");        System.out.println("\n****** Dados de compra Farmácia *******");        System.out.println("\n***************************************");        System.out.println("\nNome do Produto: " + getNome());        System.out.println("\nCódigo do Produto: + getCodigo()");        System.out.println("\nTipo de Produto: " + getTipo());        System.out.println("\nValor de Desconto: " + getDesconto());        System.out.println("\nPreço final do Produto: " + getPreco());        System.out.println("\n***************************************");    }}