package Cargo;public class TestaFuncionario {    private String funcao;    private String nomeCompleto;    private float CPF;    private float RG;    private String dataDeNascimento;    public TestaFuncionario(String funcao, String nomeCompleto, float CPF, float RG, String dataDeNascimento) {        this.funcao = funcao;        this.nomeCompleto = nomeCompleto;        this.CPF = CPF;        this.RG = RG;        this.dataDeNascimento = dataDeNascimento;    }    public String getFuncao() {        return funcao;    }    public TestaFuncionario setFuncao(String funcao) {        this.funcao = funcao;        return this;    }    public String getNomeCompleto() {        return nomeCompleto;    }    public TestaFuncionario setNomeCompleto(String nomeCompleto) {        this.nomeCompleto = nomeCompleto;        return this;    }    public float getCPF() {        return CPF;    }    public TestaFuncionario setCPF(float CPF) {        this.CPF = CPF;        return this;    }    public float getRG() {        return RG;    }    public TestaFuncionario setRG(float RG) {        this.RG = RG;        return this;    }    public String getDataDeNascimento() {        return dataDeNascimento;    }    public TestaFuncionario setDataDeNascimento(String dataDeNascimento) {        this.dataDeNascimento = dataDeNascimento;        return this;    }    public void visualizar() {        System.out.println("**********************************************");        System.out.println("********** Quadro de Funcionários ************");        System.out.println("\nFunção: " + getFuncao());        System.out.println("\nNome Completo: " + getNomeCompleto());        System.out.println("\nCPF: " + getCPF());        System.out.println("\nRG: " + getRG());        System.out.println("\nData de Nascimento: " + getDataDeNascimento());        System.out.println("**********************************************");    }}